/* eslint-env browser */

export default function(discovery) {
    function render(el, config, data, context) {
        const { content } = config;

        discovery.view.render(el, content || 'text', data, context);
    }

    discovery.view.define('alert', render);
    discovery.view.define('alert-success', render);
    discovery.view.define('alert-danger', render);
    discovery.view.define('alert-warning', render);
}

/* eslint-env browser */

export default function(discovery) {
    discovery.view.define('auto-link', function(el, config, data, context) {
        const { content } = config;

        if (!data) {
            return;
        }

        const links = discovery.resolveValueLinks(data);

        if (links) {
            discovery.view.render(el, { view: 'link', content }, links[0], context);
        } else {
            discovery.view.render(el, content || 'text', data, context);
        }
    }, {
        tag: false
    });
}

/* eslint-env browser */

function maybeFix(el, type, value) {
    if (!value) {
        return;
    }

    const prefixEl = el.appendChild(document.createElement('span'));

    prefixEl.className = type;
    prefixEl.innerText = value;
}

export default function(discovery) {
    function render(el, config, data, context) {
        const { content } = config;
        let { color, text, href, prefix, postfix } = data || {};

        if (typeof data === 'string') {
            text = data;
        }

        el.style.backgroundColor = color;

        if (href) {
            el.href = href;
        }

        maybeFix(el, 'prefix', prefix);

        if (content) {
            discovery.view.render(el, content, data, context);
        } else {
            el.appendChild(document.createTextNode(String(text)));
        }

        maybeFix(el, 'postfix', postfix);
    }

    discovery.view.define('badge', render, { tag: 'a' });
    discovery.view.define('pill-badge', render, { tag: 'a' });
}

/* eslint-env browser */

export default function(discovery) {
    discovery.view.define('block', function(el, config, data, context) {
        const { content } = config;

        discovery.view.render(el, content, data, context);
    });
}

/* eslint-env browser */

export default function(discovery) {
    function render(el, config, data, context) {
        const { content, disabled = false, onClick } = config;
        const { text = '' } = data || {};

        el.classList.add('view-button');
        el.disabled = discovery.query(disabled, data, context);
        el.addEventListener('click', () => {
            if (typeof onClick === 'function') {
                onClick(el, data, context);
            }
        });

        if (content) {
            discovery.view.render(el, content, data, context);
        } else {
            el.innerText = text;
        }
    }

    discovery.view.define('button', render, { tag: 'button' });
    discovery.view.define('button-primary', render, { tag: 'button' });
    discovery.view.define('button-danger', render, { tag: 'button' });
    discovery.view.define('button-warning', render, { tag: 'button' });
}

/* eslint-env browser */

import Highcharts from '/gen/highcharts.js';

export default function(discovery) {
    discovery.view.define('chart', function(el, config, data, context) {
        const { options } = config;
        const label = '<b>{point.y}</b> ({point.percentage:.1f}%)';

        if (!Array.isArray(data)) {
            data = data ? [data] : [];
        }

        if (data[0] && !data[0].data) {
            data = [{ data }];
        }

        try {
            Highcharts.chart(el, Highcharts.merge({
                chart: {
                    type: 'pie',
                    height: '60%'
                },
                title: {
                    text: undefined
                },
                credits: {
                    enabled: false
                },
                tooltip: {
                    pointFormat: label
                },
                plotOptions: {
                    pie: {
                        cursor: 'pointer',
                        dataLabels: {
                            format: '{point.name} – ' + label,
                            useHTMsL: true
                        }
                    }
                },
                series: data
            }, options));
        } catch (e) {
            discovery.view.render(el, { view: 'fallback', reason: e.message }, data, context);
        }
    });
}

/* eslint-env browser */

import defined from '../core/utils/defined.js';

export default function(discovery) {
    discovery.view.define('checkbox', function(el, config, data, context) {
        const { name, checked, readonly, content, onInit, onChange } = config;
        const inputEl = el.appendChild(document.createElement('input'));
        let renderContent = null;

        inputEl.type = 'checkbox';
        inputEl.checked = defined([checked !== undefined ? discovery.queryBool(checked, data, context) : undefined, context[name]], false);
        inputEl.readOnly = readonly;
        inputEl.addEventListener('click', (e) => {
            if (readonly) {
                e.preventDefault();
            }
        });
        inputEl.addEventListener('change', () => {
            if (typeof onChange === 'function') {
                onChange(inputEl.checked, name);

                if (renderContent !== null) {
                    renderContent();
                }
            }
        });

        if (typeof onInit === 'function') {
            onInit(inputEl.checked, name);
        }

        if (content) {
            const contentEl = el.appendChild(document.createElement('span'));
            renderContent = () => {
                const localContext = name ? { ...context, [name]: inputEl.checked } : context;

                contentEl.innerHTML = '';
                discovery.view.render(contentEl, content, data, localContext);
            };

            renderContent();
        }
    }, {
        tag: 'label'
    });
}

/* eslint-env browser */

export default function(discovery) {
    function render(el, config, data, context) {
        const { column = 'text', emptyText, limit } = config;

        if (emptyText !== false && emptyText !== '') {
            el.setAttribute('emptyText', emptyText || 'Empty');
        }

        if (!Array.isArray(data) && data) {
            data = [data];
        }

        if (Array.isArray(data)) {
            discovery.view.renderList(el, {
                view: 'column',
                content: column
            }, data, context, 0, discovery.view.listLimit(limit, 25));
        }
    }

    discovery.view.define('columns', render);
    discovery.view.define('column', function(el, config, data, context) {
        discovery.view.render(el, config.content, data, context);
    });
}

/* eslint-env browser */

export default function(discovery) {
    discovery.view.define('content-filter', function(el, config, data, context) {
        const { name = 'filter', type = 'regexp', placeholder, content } = config;

        discovery.view.render(el, {
            view: 'context',
            modifiers: {
                view: 'input',
                name,
                type,
                placeholder: placeholder || 'Filter'
            },
            content: {
                view: 'block',
                className: 'content',
                content
            }
        }, data, context);
    });
}

/* eslint-env browser */

export default function(discovery) {
    discovery.view.define('context', function(el, config, data, context) {
        function renderContent() {
            const container = contentStartMarker.parentNode;
            let cursor = contentEndMarker.previousSibling;

            // clear old content
            // TODO: replace for Range
            while (cursor && cursor !== contentStartMarker) {
                cursor = cursor.previousSibling;
                container.removeChild(cursor.nextSibling);
            }

            // render new content
            const buffer = document.createDocumentFragment();
            discovery.view
                .render(buffer, content, data, localContext)
                .then(() => contentEndMarker.parentNode.insertBefore(buffer, contentEndMarker));
        }

        function updateContext(value, name) {
            if (name) {
                localContext[name] = value;

                if (inited) {
                    renderContent();
                }
            }
        }

        let localContext = { ...context };
        let contentStartMarker = null;
        let contentEndMarker = null;
        let inited = false;
        let { modifiers = [] } = config;
        const { content } = config;

        if (!Array.isArray(modifiers)) {
            modifiers = [modifiers];
        }

        const awaitRender = discovery.view.render(el, modifiers.map(item => ({
            onInit: updateContext,
            onChange: updateContext,
            ...item
        })), data, context);

        contentStartMarker = el.appendChild(document.createComment('context view content start'));
        contentEndMarker = el.appendChild(document.createComment('context view content end'));
        awaitRender.then(() => {
            inited = true;
            renderContent();
        });
    }, {
        tag: false
    });
}

