/* eslint-env browser */

import CodeMirror from '/gen/codemirror.js'; // FIXME: generated file to make it local

const HINT_ELEMENT_CLASS = 'CodeMirror-hint';
const ACTIVE_HINT_ELEMENT_CLASS = 'active';
const requestAnimationFrame = window.requestAnimationFrame || (fn => setTimeout(fn, 1000 / 60));
const cancelAnimationFrame = window.cancelAnimationFrame || clearTimeout;

CodeMirror.defineExtension('showHint', function(options) {
    options = {
        closeCharacters: /[\s()\[\]{};:>,]/,
        closeOnUnfocus: true,
        container: null,
        ...this.options.hintOptions,
        ...options
    };

    const selections = this.listSelections();
    if (selections.length > 1) {
        return;
    }

    // By default, don't allow completion when something is selected.
    if (this.somethingSelected()) {
        return;
    }

    if (this.state.completionActive) {
        this.state.completionActive.close();
    }

    if (!options.hint) {
        return;
    }

    const completion = this.state.completionActive = new Completion(this, options);

    CodeMirror.signal(this, 'startCompletion', this);
    completion.update(true);
});

class Completion {
    constructor(cm, options) {
        this.cm = cm;
        this.options = options;
        this.widget = null;
        this.debounce = 0;
        this.tick = 0;
        this.startPos = this.cm.getCursor('start');
        this.startLen = this.cm.getLine(this.startPos.line).length - this.cm.getSelection().length;

        cm.on('cursorActivity', this.activityFunc = () => this.cursorActivity());
    }

    close() {
        if (!this.active()) {
            return;
        }

        this.cm.state.completionActive = null;
        this.tick = null;
        this.cm.off('cursorActivity', this.activityFunc);

        if (this.widget) {
            if (this.data) {
                CodeMirror.signal(this.data, 'close');
            }

            this.widget.close();
        }

        CodeMirror.signal(this.cm, 'endCompletion', this.cm);
    }

    active() {
        return this.cm.state.completionActive === this;
    }

    pick(data, idx) {
        const completion = data.list[idx];

        if (completion.hint) {
            completion.hint(this.cm, data, completion);
        } else {
            this.cm.replaceRange(
                getText(completion),
                completion.from || data.from,
                completion.to || data.to,
                'complete'
            );
        }

        CodeMirror.signal(data, 'pick', completion);
        this.close();
    }

    cursorActivity() {
        if (this.debounce) {
            cancelAnimationFrame(this.debounce);
            this.debounce = 0;
        }

        const pos = this.cm.getCursor();
        const line = this.cm.getLine(pos.line);
        if (pos.line != this.startPos.line || line.length - pos.ch != this.startLen - this.startPos.ch ||
            pos.ch < this.startPos.ch || this.cm.somethingSelected() ||
            (!pos.ch || this.options.closeCharacters.test(line.charAt(pos.ch - 1)))) {
            this.close();
        } else {
            this.debounce = requestAnimationFrame(() => this.update());
            if (this.widget) {
                this.widget.disable();
            }
        }
    }

    update(first) {
        if (this.tick == null) {
            return;
        }

        const myTick = ++this.tick;
        Promise.resolve(this.options.hint(this.cm, this.options)).then((data) => {
            if (this.tick == myTick) {
                this.finishUpdate(data, first);
            }
        });
    }

    finishUpdate(data) {
        if (this.data) {
            CodeMirror.signal(this.data, 'update');
        }

        const picked = this.widget && this.widget.picked;

        if (this.widget) {
            this.widget.close();
        }

        this.data = data;

        if (data && data.list.length) {
            if (picked && data.list.length == 1) {
                this.pick(data, 0);
            } else {
                this.widget = new Widget(this, data);
                CodeMirror.signal(data, 'shown');
            }
        }
    }
};

function getText(completion) {
    if (typeof completion == 'string') {
        return completion;
    }

    return completion.text;
}

function getHintElement(hintsElement, el) {
    while (el && el.parentNode !== hintsElement) {
        el = el.parentNode;
    }

    return el;
}

class Widget {
    constructor(completion, data) {
        const cm = completion.cm;
        const hintsEl = this.hintsEl = document.createElement('ul');

        this.completion = completion;
        this.data = data;
        this.picked = false;
        this.selectedHint = data.selectedHint || 0;

        hintsEl.className = 'CodeMirror-hints ' + completion.cm.options.theme;
        (completion.options.container || document.body).appendChild(hintsEl);

        this.items = data.list.map((cur, idx) => {
            const el = hintsEl.appendChild(document.createElement('li'));

            el.className = HINT_ELEMENT_CLASS;

            if (idx === this.selectedHint) {
                el.classList.add(ACTIVE_HINT_ELEMENT_CLASS);
            }

            if (cur.render) {
                cur.render(el, data, cur);
            } else {
                el.appendChild(document.createTextNode(cur.displayText || getText(cur)));
            }

            return el;
        });

        cm.addKeyMap(this.keyMap = {
            Up: () => this.changeActive(this.selectedHint - 1),
            Down: () => this.changeActive(this.selectedHint + 1),
            Enter: () => this.pick(),
            Tab: () => this.pick(),
            Esc: () => completion.close()
        });

        if (completion.options.closeOnUnfocus) {
            let closingOnBlur;
            cm.on('blur', this.onBlur = () => {
                closingOnBlur = setTimeout(() => completion.close(), 100);
            });
            cm.on('focus', this.onFocus = () => clearTimeout(closingOnBlur));
        }

        this.updatePosSize();
        document.addEventListener('scroll', this.onScroll = () => this.updatePosSize(), true);

        CodeMirror.on(hintsEl, 'mousedown', () => setTimeout(() => cm.focus(), 20));
        CodeMirror.on(hintsEl, 'click', (e) => {
            const el = getHintElement(hintsEl, e.target);
            const idx = this.items.indexOf(el);

            if (idx !== -1) {
                this.changeActive(idx);
                this.pick();
            }
        });

        CodeMirror.signal(data, 'select', data.list[this.selectedHint], this.items[this.selectedHint]);
        return true;
    }

    close() {
        if (this.completion.widget !== this) {
            return;
        }

        this.completion.widget = null;
        this.completion.cm.removeKeyMap(this.keyMap);
        this.hintsEl.remove();

        const cm = this.completion.cm;
        if (this.completion.options.closeOnUnfocus) {
            cm.off('blur', this.onBlur);
            cm.off('focus', this.onFocus);
        }

        document.removeEventListener('scroll', this.onScroll, true);
    }

    disable() {
        this.completion.cm.removeKeyMap(this.keyMap);
        this.keyMap = {
            Enter: () => this.picked = true
        };
        this.completion.cm.addKeyMap(this.keyMap);
    }

    pick() {
        this.completion.pick(this.data, this.selectedHint);
    }

    changeActive(idx, avoidWrap) {
        if (idx >= this.items.length) {
            idx = avoidWrap ? this.items.length - 1 : 0;
        } else if (idx < 0) {
            idx = avoidWrap ? 0 : this.items.length - 1;
        }

        const prev = this.items[this.selectedHint];
        const next = this.items[this.selectedHint = idx];

        if (next === prev) {
            return;
        }

        if (prev) {
            prev.classList.remove(ACTIVE_HINT_ELEMENT_CLASS);
        }

        next.classList.add(ACTIVE_HINT_ELEMENT_CLASS);

        if (next.offsetTop < this.hintsEl.scrollTop) {
            this.hintsEl.scrollTop = next.offsetTop - 3;
        } else if (next.offsetTop + next.offsetHeight > this.hintsEl.scrollTop + this.hintsEl.clientHeight) {
            this.hintsEl.scrollTop = next.offsetTop + next.offsetHeight - this.hintsEl.clientHeight + 3;
        }

        CodeMirror.signal(this.data, 'select', this.data.list[this.selectedHint], next);
    }

    updatePosSize() {
        const { completion, hintsEl, data } = this;
        const cm = completion.cm;
        let pos = cm.cursorCoords();
        let left = pos.left;
        let top = pos.bottom;
        hintsEl.style.left = left + 'px';
        hintsEl.style.top = top + 'px';
        // If we're at the edge of the screen, then we want the menu to appear on the left of the cursor.
        const winW = window.innerWidth;
        const winH = window.innerHeight;
        let box = hintsEl.getBoundingClientRect();
        let overlapY = box.bottom - winH;

        if (overlapY > 0) {
            const height = box.bottom - box.top;
            const curTop = pos.top - (pos.bottom - box.top);
            if (curTop - height > 0) { // Fits above cursor
                hintsEl.style.top = (top = pos.top - height) + 'px';
            } else if (height > winH) {
                hintsEl.style.height = (winH - 5) + 'px';
                hintsEl.style.top = (top = pos.bottom - box.top) + 'px';
                const cursor = cm.getCursor();
                if (data.from.ch != cursor.ch) {
                    pos = cm.cursorCoords(cursor);
                    hintsEl.style.left = (left = pos.left) + 'px';
                    box = hintsEl.getBoundingClientRect();
                }
            }
        }

        let overlapX = box.right - winW;
        if (overlapX > 0) {
            if (box.right - box.left > winW) {
                hintsEl.style.width = (winW - 5) + 'px';
                overlapX -= (box.right - box.left) - winW;
            }
            hintsEl.style.left = (left = pos.left - overlapX) + 'px';
        }
    }
};

CodeMirror.commands.autocomplete = CodeMirror.showHint;
CodeMirror.defineOption('hintOptions', null);

/* eslint-env browser */

import { createElement } from '../core/utils/dom.js';
import { escapeHtml } from '../core/utils/html.js';
import Emitter from '../core/emitter.js';
import CodeMirror from '/gen/codemirror.js'; // FIXME: generated file to make it local
import './editors-hint.js';

function renderQueryAutocompleteItem(el, self, { entry: { value, current, type }}) {
    const startChar = current[0];
    const lastChar = current[current.length - 1];
    const start = startChar === '"' || startChar === "'" ? 1 : 0;
    const end = lastChar === '"' || lastChar === "'" ? 1 : 0;
    const pattern = current.toLowerCase().substring(start, current.length - end);
    const offset = pattern ? value.toLowerCase().indexOf(pattern, value[0] === '"' || value[0] === "'" ? 1 : 0) : -1;

    if (offset !== -1) {
        value = (
            escapeHtml(value.substring(0, offset)) +
            '<span class="match">' + escapeHtml(value.substr(offset, pattern.length)) + '</span>' +
            escapeHtml(value.substr(offset + pattern.length))
        );
    }

    el.appendChild(createElement('span', 'name', value));
    el.appendChild(createElement('span', 'type', type));
}

class Editor extends Emitter {
    constructor(autocomplete) {
        super();

        this.el = document.createElement('div');
        this.el.className = 'discovery-editor';

        const cm = CodeMirror(this.el, {
            extraKeys: { 'Alt-Space': 'autocomplete' },
            mode: 'javascript',
            theme: 'neo',
            indentUnit: 0,
            hintOptions: autocomplete && {
                hint: autocomplete,
                completeSingle: false,
                closeOnUnfocus: true
            }
        });

        cm.on('change', () => this.emit('change', cm.getValue()));

        if (autocomplete) {
            // patch prepareSelection to inject a context hint
            // const ps = cm.display.input.prepareSelection;
            // cm.display.input.prepareSelection = function(...args) {
            //     const selection = ps.apply(this, args);
            //     if (selection.cursors.firstChild) {
            //         selection.cursors.firstChild.appendChild(createElement('div', 'context-hint', 'asd'));
            //     }
            //     return selection;
            // };

            cm.on('cursorActivity', cm => cm.state.focused && cm.showHint(autocomplete));
            cm.on('focus', cm => cm.showHint(autocomplete));
        }

        this.cm = cm;
    }

    getValue() {
        return this.cm.getValue();
    }

    setValue(value) {
        // call refresh() method to update sizes and content
        // use a Promise as zero timeout, like a setImmediate()
        Promise.resolve().then(() => this.cm.refresh());

        if (typeof value === 'string' && this.getValue() !== value) {
            this.cm.setValue(value || '');
        }
    }

    focus() {
        this.cm.focus();
    }
}

class QueryEditor extends Editor {
    constructor(discovery) {
        super(function(cm) {
            const cursor = cm.getCursor();
            const suggestions = discovery.querySuggestions(
                cm.getValue(),
                cm.doc.indexFromPos(cursor),
                discovery.data,    // TODO: fix me, should be configurable
                discovery.context  // TODO: fix me, should be configurable
            );

            if (!suggestions) {
                return;
            }

            return {
                list: suggestions.slice(0, 50).map(entry => {
                    return {
                        entry,
                        text: entry.value,
                        render: renderQueryAutocompleteItem,
                        from: cm.posFromIndex(entry.from),
                        to: cm.posFromIndex(entry.to)
                    };
                })
            };
        });
    }
}

class ViewEditor extends Editor {
    constructor() {
        super();
    }
}

export default function(discovery) {
    Object.assign(discovery.view, {
        QueryEditor,
        ViewEditor
    });
}

/* eslint-env browser */

export default function(discovery) {
    discovery.view.define('expand', function(el, config, data, context) {
        function renderState() {
            if (contentEl) {
                contentEl.remove();
                contentEl = null;
            }

            if (expanded) {
                el.classList.add('expanded');
                contentEl = document.createElement('div');
                discovery.view.render(contentEl, content, data, context);
                el.appendChild(contentEl);
            } else {
                el.classList.remove('expanded');
            }
        }

        let { expanded, title, content, onToggle } = config;
        const headerEl = el.appendChild(document.createElement('div'));
        const titleEl = headerEl.appendChild(document.createElement('div'));
        const triggerEl = headerEl.appendChild(document.createElement('div'));
        let contentEl = null;

        expanded = discovery.queryBool(expanded, data, context);

        headerEl.className = 'header';
        triggerEl.className = 'trigger';
        headerEl.addEventListener('click', () => {
            expanded = !expanded;
            renderState();

            if (typeof onToggle === 'function') {
                onToggle(expanded);
            }
        });

        discovery.view.render(titleEl, title || { view: 'text', data: '"No title"' }, data, context);
        renderState();
    });
}

/* eslint-env browser */

export default function(discovery) {
    discovery.view.define('fallback', function(el, config, data, context) {
        const { reason } = config;
        const headerEl = el.appendChild(document.createElement('div'));
        const contentEl = el.appendChild(document.createElement('div'));

        headerEl.className = 'header';
        headerEl.innerText = 'Fallback view' + (reason ? ' – ' + reason : '');

        contentEl.className = 'content';
        discovery.view.render(contentEl, 'struct', data, context);
    });
}

/* eslint-env browser */

export default function(discovery) {
    function render(el, config, data, context) {
        const { content } = config;

        discovery.view.render(el, content || 'text', data, context);
    }

    discovery.view.define('header', render, { tag: 'h2' });
    discovery.view.define('h1', render, { tag: 'h1' });
    discovery.view.define('h2', render, { tag: 'h2' });
    discovery.view.define('h3', render, { tag: 'h3' });
    discovery.view.define('h4', render, { tag: 'h4' });
}

/* eslint-env browser */

export default function(discovery) {
    discovery.view.define('hstack', function(el, config, data, context) {
        const { content } = config;

        discovery.view.render(el, content, data, context);
    });
}

/* eslint-env browser */

export default function(discovery) {
    const buffer = document.createElement('div');

    discovery.view.define('html', function(el, config, data) {
        buffer.innerHTML = data;

        while (buffer.firstChild) {
            el.appendChild(buffer.firstChild);
        }
    }, {
        tag: null
    });
}

/* eslint-env browser */

import * as base64 from '../core/utils/base64.js';

export default function(discovery) {
    discovery.view.define('image-preview', function(el, config, data) {
        let { content, binary, mime } = data || {};

        el.hidden = true;

        if (/^image\//i.test(mime)) {
            el.hidden = false;

            if (!binary) {
                content = base64.encode(content);
            }

            el.innerHTML = `<img src="data:${mime};base64,${content}">`;
        }
    });
