/* eslint-env browser */

const hasOwnProperty = Object.prototype.hasOwnProperty;
const self = data => data;

function configFromName(name) {
    return {
        header: name,
        view: 'table-cell',
        data: obj => obj[name]
    };
}

function resolveColConfig(config) {
    return typeof config === 'string'
        ? { data: self, content: config }
        : { data: self, ...config };
}

export default function(discovery) {
    discovery.view.define('table', function(el, config, data, context) {
        let { cols, limit } = config;
        let colsMap = cols && typeof cols === 'object' ? cols : {};
        let scalarCol = false;

        if (!Array.isArray(data) && data) {
            data = [data];
        }

        const headEl = el.appendChild(document.createElement('thead'));
        const bodyEl = el.appendChild(document.createElement('tbody'));
        const moreEl = el
            .appendChild(document.createElement('tbody'))
            .appendChild(document.createElement('tr'))
            .appendChild(document.createElement('td'));

        if (Array.isArray(cols)) {
            cols = cols.map((def, idx) =>
                typeof def === 'string'
                    ? configFromName(def)
                    : {
                        header: 'col' + idx,
                        view: 'table-cell',
                        data: self,
                        ...def
                    }
            );
        } else if (Array.isArray(data)) {
            const colNames = new Set();
            cols = [];

            data
                .forEach(item => {
                    if (item && typeof item === 'object') {
                        for (const key in item) {
                            colNames.add(key);
                        }
                    } else {
                        scalarCol = true;
                    }
                });

            Object.keys(colsMap)
                .forEach(name => colsMap[name] ? colNames.add(name) : colNames.delete(name));

            if (scalarCol) {
                cols.push({
                    header: '[value]',
                    view: 'table-cell',
                    data: data => String(data)
                });
            }

            colNames.forEach(name =>
                cols.push({
                    ...configFromName(name),
                    ...hasOwnProperty.call(colsMap, name) ? resolveColConfig(colsMap[name]) : null
                })
            );
        } else {
            console.warn('config.cols and data is not an array, no way to build a table');
            return;
        }

        cols.forEach(col =>
            headEl.appendChild(document.createElement('th')).innerText = col.header
        );

        moreEl.colSpan = cols.length;
        discovery.view.renderList(bodyEl, {
            view: 'table-row',
            cols
        }, data, context, 0, discovery.view.listLimit(limit, 25), moreEl);
    });
}

/* eslint-env browser */

export default function(discovery) {
    discovery.view.define('tabs', function(el, config, data, context) {
        function renderContent(value) {
            const handler = inited ? onChange : onInit;

            if (currentValue === value) {
                return;
            }

            currentValue = value;
            inited = true;

            if (Array.isArray(tabs)) {
                tabsEl.innerHTML = '';
                tabs.forEach(tab =>
                    discovery.view.render(tabsEl, {
                        view: 'tab',
                        active: tab.value === currentValue,
                        content: tab.content,
                        onClick: renderContent
                    }, tab, context)
                );
            }

            if (content) {
                contentEl.innerHTML = '';
                discovery.view.render(contentEl, content, data, { ...context, [name]: value });
            }

            if (typeof handler === 'function') {
                handler(currentValue, name);
            }
        }

        const tabsEl = el.appendChild(document.createElement('div'));
        const contentEl = el.appendChild(document.createElement('div'));
        const { content, onInit, onChange } = config;
        let { name, tabs } = config;
        let inited = false;
        let currentValue = NaN;
        let initValue =
            'value' in config
                ? config.value
                : name in context
                    ? context[name]
                    : undefined;

        tabsEl.className = 'view-tabs-buttons';
        contentEl.className = 'view-tabs-content';

        if (typeof name !== 'string') {
            name = 'filter';
        }

        if (Array.isArray(tabs)) {
            tabs = tabs.map(tab => {
                const type = typeof tab;

                if (type === 'string' || type === 'number' || type === 'boolean') {
                    tab = { value: tab };
                }

                if (initValue === undefined || tab.active) {
                    initValue = tab.value;
                }

                return tab;
            });
        } else {
            tabs = [];
        }

        renderContent(initValue);
    });
}

/* eslint-env browser */

const toString = Object.prototype.toString;

export default function(discovery) {
    const matchWrapperEl = document.createElement('span');
    matchWrapperEl.className = 'view-text-match';

    discovery.view.define('text-match', function(el, config, data) {
        const { text, match } = data;
        const parts = toString.call(match) === '[object RegExp]'
            ? String(text).split(match)
            : [String(text)];

        parts.forEach((text, idx) => {
            if (text !== '') {
                let container = el;

                if (idx % 2) {
                    container = container.appendChild(matchWrapperEl.cloneNode());
                }

                container.appendChild(document.createTextNode(text));
            }
        });
    }, {
        tag: false
    });
}

