/* eslint-env browser */

import { createElement } from '../core/utils/dom.js';

export default function(discovery) {
    const elementToData = new WeakMap();
    const clickHandler = ({ target }) => {
        const toggleEl = target.closest('.view-tree-leaf-toggle');

        if (toggleEl) {
            const state = elementToData.get(toggleEl);
            const expanded = !toggleEl.parentNode.classList.toggle('collapsed');

            if (typeof state.render === 'function') {
                state.render();
            }

            if (typeof state.onToggle === 'function') {
                state.onToggle(expanded, toggleEl.parentNode, state.data, state.context);
            }
        }
    };

    // single event handler for all `tree-leaf` view instances
    document.addEventListener('click', clickHandler, false);

    discovery.view.define('tree-leaf', function(el, config, data, context) {
        const { expanded, content, collapsible = true, last, hasChildren, children, limit, onToggle } = config;
        const toggleEl = el.appendChild(createElement('span', 'view-tree-leaf-toggle'));
        const contentEl = el.appendChild(createElement('span', 'view-tree-leaf-content'));
        let childrenData = null;
        let hasChildrenEl = hasChildren;

        if (last) {
            el.classList.add('last');
        }

        if (!collapsible) {
            el.classList.add('non-collapsible');
        }

        discovery.view.render(contentEl, content, data, context);

        if (children) {
            childrenData = discovery.query(children, data, context);
            hasChildrenEl = Array.isArray(childrenData) && childrenData.length > 0;
        }

        if (hasChildrenEl) {
            const childrenEl = el.appendChild(createElement('ul', 'view-tree-leaf-children'));
            const state = { data, context, onToggle, render: null };
            const renderChildren = function(data, expanded) {
                if (typeof expanded === 'number') {
                    expanded--;
                }

                discovery.view.renderList(childrenEl, {
                    view: 'tree-leaf',
                    expanded,
                    content,
                    collapsible,
                    children,
                    limit,
                    onToggle
                }, data, context, 0, discovery.view.listLimit(limit, 25));
            };

            el.classList.add('has-children');
            elementToData.set(toggleEl, state);

            if (typeof expanded === 'function' ? expanded(data, context) : expanded) {
                if (childrenData) {
                    renderChildren(childrenData, expanded);
                }
            } else {
                el.classList.add('collapsed');

                if (childrenData) {
                    state.render = () => {
                        state.render = null;
                        renderChildren(childrenData, expanded || 1);
                    };
                }
            }
        }
    }, {
        tag: 'li'
    });
}

/* eslint-env browser */

export default function(discovery) {
    function renderTreeLines(container, renderElStack, leafBaseConfig, data, context, offset, limit) {
        if (limit === false) {
            limit = data.length;
        }

        data
            .slice(offset, offset + limit)
            .reduce(
                (pipeline, entry) => pipeline.then(() => {
                    const targetContainer = renderElStack[0];

                    return discovery.view
                        .render(targetContainer, {
                            ...leafBaseConfig,
                            expanded: entry.expanded,
                            last: entry.last,
                            hasChildren: entry.hasChildren,
                            children: entry.children
                        }, entry.data, context)
                        .then(() => {
                            if (entry.expanded && entry.hasChildren) {
                                const container = targetContainer.lastChild.querySelector('.view-tree-leaf-children');

                                container.classList.add('incomplete');
                                renderElStack.unshift(container);
                            } else {
                                while (entry.shift--) {
                                    renderElStack.shift().classList.remove('incomplete');
                                }
                            }
                        });
                }),
                Promise.resolve()
            )
            .then(() =>
                discovery.view.maybeMoreButtons(
                    container,
                    null,
                    data.length,
                    offset + limit,
                    limit,
                    (offset, limit) => renderTreeLines(container, renderElStack, leafBaseConfig, data, context, offset, limit)
                )
            );
    }

    function buildTreeLines(data, context, childrenGetter, expanded) {
        function processChildren(array, expanded, popCount = 0) {
            array.forEach((data, index, array) => {
                const children = discovery.query(childrenGetter, data, context);
                const hasChildren = Array.isArray(children) && children.length > 0;
                const last = index === array.length - 1;
                const leafExpanded =
                    visited.has(data)
                        ? 0
                        : typeof expanded === 'function'
                            ? expanded(data, context)
                            : expanded;

                visited.add(data);
                leafs.push({
                    data,
                    expanded: leafExpanded,
                    last,
                    hasChildren,
                    children: leafExpanded ? null : childrenGetter,
                    shift: last && (!leafExpanded || !hasChildren) ? popCount + 1 : 0
                });

                if (hasChildren && leafExpanded) {
                    if (typeof expanded === 'number') {
                        expanded--;
                    }

                    processChildren(children, expanded, last ? popCount + 1 : 0);
                }
            });
        }

        const leafs = [];
        const visited = new Set();

        processChildren(data, expanded);

        return leafs;
    }

    discovery.view.define('tree', function render(el, config, data, context) {
        const { children, item = 'text', collapsible, emptyText, onToggle } = config;
        let { expanded, limit, limitLines = true } = config;

        if (emptyText !== false && emptyText !== '') {
            el.setAttribute('emptyText', emptyText || 'Empty tree');
        }

        if (!Array.isArray(data) && data) {
            data = [data];
        }

        if (Array.isArray(data)) {
            limit = discovery.view.listLimit(limit, 25);
            limitLines = discovery.view.listLimit(limitLines, 25);
            expanded = typeof expanded === 'function' ? expanded : discovery.view.listLimit(expanded, 1);

            if (limitLines) {
                const lines = buildTreeLines(data, context, children, expanded);
                const leafBaseConfig = {
                    view: 'tree-leaf',
                    content: item,
                    collapsible,
                    onToggle
                };

                renderTreeLines(el, [el], leafBaseConfig, lines, context, 0, limitLines);
            } else {
                discovery.view.renderList(el, {
                    view: 'tree-leaf',
                    content: item,
                    collapsible,
                    expanded,
                    children,
                    limit,
                    onToggle
                }, data, context, 0, limit);
            }
        }
    }, {
        tag: 'ul'
    });
}
